import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button, Alert, Nav, NavDropdown } from 'react-bootstrap';
import Login from './components/Login';
import Lobby from './components/Lobby';
import Game from './components/Game';
import SpectateGame from './components/SpectateGame';
import UserProfile from './components/UserProfile';
import GameHistory from './components/GameHistory';
import './App.css';

function App() {
  const [currentScreen, setCurrentScreen] = useState('login');
  const [socket, setSocket] = useState(null);
  const [user, setUser] = useState({
    username: '',
    playerId: null,
    isAuthenticated: false,
    isRegistered: false,
    profile: null
  });
  const [gameData, setGameData] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const [spectateGameId, setSpectateGameId] = useState(null);
  const [gameHistory, setGameHistory] = useState([]);

  // Connect to WebSocket server
  useEffect(() => {
    const newSocket = new WebSocket('ws://localhost:8765');
    
    newSocket.onopen = () => {
      console.log('Connected to WebSocket server');
    };
    
    newSocket.onclose = () => {
      console.log('Disconnected from WebSocket server');
      setErrorMessage('Lost connection to server. Please refresh the page.');
    };
    
    newSocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      setErrorMessage('Connection error. Please check if the server is running.');
    };
    
    newSocket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Received message:', data);
      
      if (data.type === 'auth_response') {
        if (data.success) {
          const userUpdate = {
            ...user,
            playerId: data.player_id,
            isAuthenticated: true,
            isRegistered: !!data.user_profile // If user_profile exists, this is a registered user
          };
          
          if (data.user_profile) {
            userUpdate.profile = data.user_profile;
          }
          
          setUser(userUpdate);
          setCurrentScreen('lobby');
        } else {
          setErrorMessage(data.message);
        }
      }
      
      else if (data.type === 'game_start') {
        setGameData({
          gameId: data.game_id,
          color: data.color,
          opponent: data.opponent,
          state: data.state
        });
        setCurrentScreen('game');
      }
      
      else if (data.type === 'game_update') {
        if (gameData) {
          setGameData({
            ...gameData,
            state: data.state
          });
        }
      }
      
      else if (data.type === 'spectate_game') {
        setGameData({
          gameId: data.game_id,
          isSpectator: true,
          state: data.state,
          chatHistory: data.chat_history
        });
        setCurrentScreen('spectate');
      }
      
      else if (data.type === 'user_games') {
        setGameHistory(data.games);
      }
      
      else if (data.type === 'user_stats') {
        setUser({
          ...user,
          profile: {
            ...user.profile,
            ...data.stats
          }
        });
      }
      
      else if (data.type === 'error') {
        setErrorMessage(data.message);
        setTimeout(() => setErrorMessage(null), 5000);
      }
    };
    
    setSocket(newSocket);
    
    return () => {
      newSocket.close();
    };
  }, []);
  
  const handleLogin = (username, password, isRegistering) => {
    if (socket) {
      if (isRegistering) {
        // Send registration request
        socket.send(JSON.stringify({
          type: 'register',
          username: username,
          password: password
        }));
      } else if (password) {
        // Send login request (with password)
        socket.send(JSON.stringify({
          type: 'login',
          username: username,
          password: password
        }));
      } else {
        // Guest login (no password)
        socket.send(JSON.stringify({
          type: 'register',
          username: username
        }));
      }
      
      setUser({
        ...user,
        username: username
      });
    }
  };
  
  const handleFindMatch = () => {
    if (socket && user.isAuthenticated) {
      socket.send(JSON.stringify({
        type: 'find_match'
      }));
    }
  };
  
  const handleSpectate = (gameId) => {
    if (socket && user.isAuthenticated) {
      socket.send(JSON.stringify({
        type: 'spectate',
        game_id: gameId
      }));
      setSpectateGameId(gameId);
    }
  };
  
  const handleMove = (move) => {
    if (socket && gameData) {
      socket.send(JSON.stringify({
        type: 'make_move',
        move: move
      }));
    }
  };
  
  const handleSendChat = (message) => {
    if (socket && gameData) {
      socket.send(JSON.stringify({
        type: 'chat',
        message: message
      }));
    }
  };
  
  const handleLoadUserGames = () => {
    if (socket && user.isAuthenticated) {
      socket.send(JSON.stringify({
        type: 'get_user_games'
      }));
    }
  };
  
  const handleLoadUserStats = () => {
    if (socket && user.isAuthenticated && user.isRegistered) {
      socket.send(JSON.stringify({
        type: 'get_user_stats'
      }));
    }
  };
  
  const handleLogout = () => {
    if (socket) {
      // Close the current socket connection
      socket.close();
      
      // Reset user state
      setUser({
        username: '',
        playerId: null,
        isAuthenticated: false,
        isRegistered: false,
        profile: null
      });
      
      // Reset game data
      setGameData(null);
      setSpectateGameId(null);
      setGameHistory([]);
      
      // Go back to login screen
      setCurrentScreen('login');
      
      // Create a new socket connection
      const newSocket = new WebSocket('ws://localhost:8765');
      setSocket(newSocket);
    }
  };
  
  const goToLobby = () => {
    setCurrentScreen('lobby');
    setGameData(null);
    setSpectateGameId(null);
  };
  
  return (
    <Container fluid className="app-container">
      <Row className="header">
        <Col>
          <h1 className="text-center">Multiplayer Chess</h1>
        </Col>
      </Row>
      
      {user.isAuthenticated && (
        <Row className="navigation-bar">
          <Col>
            <Nav variant="tabs">
              <Nav.Item>
                <Nav.Link 
                  onClick={() => setCurrentScreen('lobby')} 
                  active={currentScreen === 'lobby'}
                >
                  Lobby
                </Nav.Link>
              </Nav.Item>
              {gameData && (
                <Nav.Item>
                  <Nav.Link 
                    onClick={() => setCurrentScreen(spectateGameId ? 'spectate' : 'game')} 
                    active={currentScreen === 'game' || currentScreen === 'spectate'}
                  >
                    {spectateGameId ? 'Spectating' : 'Playing'} Game
                  </Nav.Link>
                </Nav.Item>
              )}
              {user.isRegistered && (
                <>
                  <Nav.Item>
                    <Nav.Link 
                      onClick={() => {
                        handleLoadUserGames();
                        setCurrentScreen('history');
                      }}
                      active={currentScreen === 'history'}
                    >
                      Game History
                    </Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link 
                      onClick={() => {
                        handleLoadUserStats();
                        setCurrentScreen('profile');
                      }}
                      active={currentScreen === 'profile'}
                    >
                      Profile
                    </Nav.Link>
                  </Nav.Item>
                </>
              )}
            </Nav>
          </Col>
          <Col xs="auto" className="user-info">
            <span>
              Logged in as: <strong>{user.username}</strong>
              {user.isRegistered ? ' (Registered)' : ' (Guest)'}
            </span>
            <Button variant="outline-secondary" size="sm" className="ms-3" onClick={handleLogout}>
              Logout
            </Button>
          </Col>
        </Row>
      )}
      
      {errorMessage && (
        <Row className="mt-3">
          <Col>
            <Alert variant="danger">{errorMessage}</Alert>
          </Col>
        </Row>
      )}
      
      <Row className="content-area">
        <Col>
          {currentScreen === 'login' && (
            <Login onLogin={handleLogin} />
          )}
          
          {currentScreen === 'lobby' && (
            <Lobby 
              onFindMatch={handleFindMatch} 
              onSpectate={handleSpectate}
              socket={socket}
              isRegistered={user.isRegistered}
            />
          )}
          
          {currentScreen === 'game' && gameData && (
            <Game 
              gameData={gameData} 
              onMove={handleMove} 
              onSendChat={handleSendChat}
              socket={socket}
            />
          )}
          
          {currentScreen === 'spectate' && gameData && (
            <SpectateGame 
              gameData={gameData} 
              onSendChat={handleSendChat}
              onBack={goToLobby}
              socket={socket}
            />
          )}
          
          {currentScreen === 'history' && (
            <GameHistory 
              games={gameHistory}
              username={user.username}
              onLoadGames={handleLoadUserGames}
            />
          )}
          
          {currentScreen === 'profile' && (
            <UserProfile 
              userProfile={user.profile}
              onLoadStats={handleLoadUserStats}
            />
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
