import React, { useState } from 'react';
import { Form, Button, Card, Tabs, Tab, Alert } from 'react-bootstrap';

function Login({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [activeTab, setActiveTab] = useState('login');
  const [error, setError] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim() && password.trim()) {
      onLogin(username, password, false);
    }
  };

  const handleRegister = (e) => {
    e.preventDefault();
    
    // Validate form
    if (!username.trim()) {
      setError('Username is required');
      return;
    }
    
    if (!password.trim()) {
      setError('Password is required');
      return;
    }
    
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    
    // Clear any previous errors
    setError('');
    
    // Call the login handler with registration flag
    onLogin(username, password, true);
  };

  return (
    <div className="auth-container">
      <Card>
        <Card.Header as="h5">Welcome to Multiplayer Chess</Card.Header>
        <Card.Body>
          <Tabs
            activeKey={activeTab}
            onSelect={(key) => setActiveTab(key)}
            className="mb-3"
          >
            <Tab eventKey="login" title="Login">
              <p className="mb-3">Sign in with your account to play or spectate games.</p>
              
              <Form onSubmit={handleLogin}>
                <Form.Group className="mb-3">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter your username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    autoFocus
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </Form.Group>
                
                <Button variant="primary" type="submit" disabled={!username.trim() || !password.trim()}>
                  Login
                </Button>
                
                <div className="mt-3 text-center">
                  <small>Don't have an account? <Button variant="link" size="sm" className="p-0" onClick={() => setActiveTab('register')}>Register</Button></small>
                </div>
              </Form>
            </Tab>
            
            <Tab eventKey="register" title="Register">
              <p className="mb-3">Create a new account to track your games and statistics.</p>
              
              {error && <Alert variant="danger">{error}</Alert>}
              
              <Form onSubmit={handleRegister}>
                <Form.Group className="mb-3">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Choose a username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Choose a password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Confirm Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Confirm your password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                  />
                </Form.Group>
                
                <Button variant="success" type="submit">
                  Register
                </Button>
                
                <div className="mt-3 text-center">
                  <small>Already have an account? <Button variant="link" size="sm" className="p-0" onClick={() => setActiveTab('login')}>Login</Button></small>
                </div>
              </Form>
            </Tab>
            
            <Tab eventKey="guest" title="Play as Guest">
              <p className="mb-3">Play without an account. Your games won't be saved.</p>
              
              <Form onSubmit={(e) => { e.preventDefault(); onLogin(username, '', false); }}>
                <Form.Group className="mb-3">
                  <Form.Label>Guest Username</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter a temporary username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </Form.Group>
                
                <Button variant="secondary" type="submit" disabled={!username.trim()}>
                  Play as Guest
                </Button>
              </Form>
            </Tab>
          </Tabs>
        </Card.Body>
      </Card>
    </div>
  );
}

export default Login;
