import React, { useEffect, useRef } from 'react';

function ChatWindow({ messages, currentUser, isSpectator = false }) {
  const chatEndRef = useRef(null);
  
  // Auto-scroll to the bottom when new messages arrive
  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);
  
  // Format timestamp to readable time
  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };
  
  // Determine message styling based on sender
  const getMessageClass = (sender) => {
    if (isSpectator) {
      return 'chat-message spectator';
    }
    
    return sender === currentUser ? 'chat-message own' : 'chat-message opponent';
  };
  
  return (
    <div className="chat-window">
      {messages.length === 0 ? (
        <p className="text-center text-muted">No messages yet</p>
      ) : (
        messages.map((message, index) => (
          <div key={index} className={getMessageClass(message.sender)}>
            <span className="sender">{message.sender}:</span>
            {message.message}
            <span className="timestamp">{formatTimestamp(message.timestamp)}</span>
          </div>
        ))
      )}
      <div ref={chatEndRef}></div>
    </div>
  );
}

export default ChatWindow;
