import React, { useState, useEffect } from 'react';
import { Card, Button, ListGroup, Spinner, Row, Col, Badge, Alert } from 'react-bootstrap';

function Lobby({ onFindMatch, onSpectate, socket, isRegistered }) {
  const [availableGames, setAvailableGames] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [waitingMessage, setWaitingMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (socket) {
      // Ask for the list of games when component mounts
      socket.send(JSON.stringify({ type: 'list_games' }));

      // Setup listener for lobby status messages
      const messageHandler = (event) => {
        const data = JSON.parse(event.data);
        
        if (data.type === 'lobby_status') {
          setIsSearching(true);
          setWaitingMessage(`Waiting for an opponent... Queue position: ${data.queue_position}`);
        }
        else if (data.type === 'games_list') {
          setAvailableGames(data.games);
          setIsLoading(false);
        }
        else if (data.type === 'game_start') {
          setIsSearching(false);
        }
      };

      socket.addEventListener('message', messageHandler);

      // Refresh the list every 10 seconds
      const interval = setInterval(() => {
        socket.send(JSON.stringify({ type: 'list_games' }));
      }, 10000);

      return () => {
        socket.removeEventListener('message', messageHandler);
        clearInterval(interval);
      };
    }
  }, [socket]);

  const handleFindMatch = () => {
    setIsSearching(true);
    setWaitingMessage('Finding an opponent...');
    onFindMatch();
  };

  const handleSpectate = (gameId) => {
    onSpectate(gameId);
  };

  const formatGameInfo = (game) => {
    return `${game.white_player} vs ${game.black_player} • Moves: ${game.move_count}`;
  };

  return (
    <div className="lobby-container">
      <Card className="mb-4">
        <Card.Header as="h5">Play Chess</Card.Header>
        <Card.Body>
          <Row>
            <Col md={8}>
              <Card.Text>
                Join the matchmaking queue to play against another player, or spectate an ongoing game.
              </Card.Text>
              {isSearching ? (
                <div className="waiting-indicator">
                  <Spinner animation="border" size="sm" className="waiting-spinner" />
                  <span>{waitingMessage}</span>
                </div>
              ) : (
                <Button 
                  variant="success" 
                  size="lg" 
                  onClick={handleFindMatch}
                >
                  Find a Match
                </Button>
              )}
            </Col>
            <Col md={4}>
              <div className="account-status p-3 bg-light rounded">
                <h6>Account Status</h6>
                {isRegistered ? (
                  <>
                    <Badge bg="success">Registered</Badge>
                    <p className="small mt-2 mb-0">
                      Your games and stats will be saved to your profile.
                    </p>
                  </>
                ) : (
                  <>
                    <Badge bg="warning" text="dark">Guest</Badge>
                    <p className="small mt-2 mb-0">
                      Playing as guest. Game history will not be saved.
                    </p>
                  </>
                )}
              </div>
            </Col>
          </Row>
          
          {!isRegistered && (
            <Alert variant="info" className="mt-3">
              <Alert.Heading>Want to save your games?</Alert.Heading>
              <p>
                Log out and create an account to track your game history and statistics.
                Registered users can replay their past games and see their win/loss record.
              </p>
            </Alert>
          )}
        </Card.Body>
      </Card>

      <Card>
        <Card.Header as="h5">Spectate Games</Card.Header>
        <Card.Body>
          {isLoading ? (
            <div className="text-center">
              <Spinner animation="border" />
              <p>Loading available games...</p>
            </div>
          ) : availableGames.length > 0 ? (
            <ListGroup className="game-list">
              {availableGames.map((game) => (
                <ListGroup.Item key={game.game_id} className="game-list-item">
                  <div>
                    {formatGameInfo(game)}
                    {game.spectator_count > 0 && (
                      <Badge bg="info" className="ms-2">
                        {game.spectator_count} {game.spectator_count === 1 ? 'spectator' : 'spectators'}
                      </Badge>
                    )}
                  </div>
                  <Button 
                    variant="outline-primary" 
                    size="sm" 
                    onClick={() => handleSpectate(game.game_id)}
                  >
                    Spectate
                  </Button>
                </ListGroup.Item>
              ))}
            </ListGroup>
          ) : (
            <p className="text-center">No active games to spectate at the moment.</p>
          )}
        </Card.Body>
        <Card.Footer>
          <Button 
            variant="light" 
            size="sm" 
            onClick={() => socket.send(JSON.stringify({ type: 'list_games' }))}
          >
            Refresh List
          </Button>
        </Card.Footer>
      </Card>
    </div>
  );
}

export default Lobby;
